import './App.css';
import React, { useState } from "react";



const App = () => {

    const [uname,setName] = useState('');
    const [pass,setP] = useState('');
    const [userName,setFname]=useState('');
    const [passWord,setPW]=useState('');

    const inputUname = (event) => {
        setName(event.target.value);
    };
    const inputPass = (event) => {
        setP(event.target.value);
    };

    const submit = (event) => {
        event.preventDefault();
        setFname(uname);
        setPW(pass);
    };

    return (
        <>
        <form onSubmit={submit}>
        <div className='root'>
        <h1 className="Heading">Hello {userName} {passWord}</h1>
        <input type="text" onChange = {inputUname} value = {uname}/>
        <input type="text" onChange = {inputPass} value = {pass}/>
        <button type='submit'>Submit me</button>
        </div>
        </form>
        </>
    );
}

export default App;
