import './App.css';
import React, { useState } from "react";



const App = () => {

    const [uname,setName]=useState('');
    const [fullNmae,setFname]=useState('');

    const inputChange = (evt) => {
        setName(evt.target.value);
    }

    const submit = () => {
        setFname(uname);
    }

    return (
        <>
        <div className='root'>
        <h1 className="Heading">Hello {fullNmae}</h1>
        <input type="text" onChange={inputChange} value={uname}/>
        <input type="text" onChange={inputChange} value={uname}/>
        <button onClick={submit}>click me</button>
        </div>
        </>
    );
}

export default App;
