import './App.css';
import React, { useState } from "react";



const App = () => {

    const [uname,setName]=useState('');
    const [userName,setFname]=useState('');
    const [pass,setP]=useState('');
    const [passWord,setPW]=useState();

    const inputUname = (evt) => {
        setName(evt.target.value);
    }
    const inputPass = (evt) => {
        setP(evt.taregt.value);
    }

    const submit = () => {
        setFname(uname);
        setPW(pass);
    }

    return (
        <>
        <div className='root'>
        <h1 className="Heading">Hello {userName} {passWord}</h1>
        <input type="text" onChange={inputUname} value={uname}/>
        <input type="text" onChange={inputPass} value={pass}/>
        <button onClick={submit}>click me</button>
        </div>
        </>
    );
}

export default App;
