import './App.css';
import React, { useState } from "react";



const App = () => {
    const [bgclr,setBgclr] = useState("#8e44ad");
    const [,setName] = useState("#8e44ad");
    const single = () => {
        setBgclr("#34495e");
    }

    return (
        <>
        <div className='root' style = {{ backgroundColor : bgclr}}>
        {/* <h1 className="Heading">{time}</h1> */}
        <button onClick={single} >{text}</button>
        </div>
        </>
    );
}

export default App;
