import './App.css';
import React, { useState } from "react";



const App = () => {

    let [uname,setName]=useState('');
    let [pass,setP]=useState('');
    let [userName,setFname]=useState();
    let [passWord,setPW]=useState();

    const inputUname = (evtU) => {
        setName(evtU.target.value);
    }
    const inputPass = (evtP) => {
        setP(evtP.taregt.value);
    }

    const submit = (event) => {
        prevent
        setFname(uname);
        setPW(pass);
    }

    return (
        <>
        <form onSubmit={submit}>
        <div className='root'>
        <h1 className="Heading">Hello {userName} {passWord}</h1>
        <input type="text" onChange={inputUname} value={uname}/>
        <input type="text" onChange={inputPass} value={pass}/>
        <button type='submit'>Submit me</button>
        </div>
        </form>
        </>
    );
}

export default App;
