    const [insert, setInsert] = useState([]);
    const [ins, setIns] = useState('');
    const catchIn = (event) => setIns(event.target.value);
    const add = () => {
        setInsert((oldData) => {
            return [...oldData, ins];
        });
        setIns('');
    }
