        })
    }
    const delt = (id) => {
        setInsert((oldData) => {
            return  oldData.filter((cVal,index) => {
                return index !== id;
            })
        })
    };
    return (
        <>
            <div className="todoList">
                <h1 className="heading">todo list</h1>
                <div className='inputFiled'>
                    <input placeholder='this is input field' value={ins} onChange={catchIn} /><button onClick={add}>+</button>
                </div>
                <ul className='uoList'>
                    {insert.map((val,index)=>{
                        return <List key={index} id={index} value={val} cut={lineThrough} delete={delt}/>
                    })}
                </ul>
            </div>
        </>
    );
}

export default App;
