import React, { useState } from 'react';
import List from './List'
import "./App.css"
const App = () => {
    const [insert, setInsert] = useState([]);
    const [ins, setIns] = useState('');
    const [hide, setHide] = useState(false);
    const catchIn = (event) => {
        // console.log(event.target.value);
        setIns(event.target.value);
    };
    const add = () => {
        setHide(true);
        setInsert((oldData) => {
            return [...oldData, ins];
        });
        setIns('');
        const delt = (id) => {
            console.log('deleted');

        };
    }
    return (
        <>
            <div className="todoList">
                <h1 className="heading">todo list</h1>
                <div className='inputFiled'>
                    <input placeholder='this is input field' value={ins} onChange={catchIn} /><button onClick={add}>+</button>
                </div>
                <ul className='uoList'>
                    {insert.map((val,index)=>{
                        return <List key={index} id={index} value={val} delete={delt} hidden={hide}/>
                    })}
                </ul>
            </div>
        </>
    );
}

export default App;
