import './App.css';
import React, { useState } from "react";



const App = () => {
    const [bgclr,setBg] = useState("#8e44ad");
    const [text,setTxt] = useState("Click Me");
    const single = () => {
        setBg("#34495e");
        setTxt("Ouch!! 😱");
    }
    const double = () => {
        setBg('#8e44ad');
        setTxt('Yehh 😏');
    }

    return (
        <>
        <div className='root' style = {{ backgroundColor : bgclr}}>
        {/* <h1 className="Heading">{time}</h1> */}
        <button onClick={single} onDoubleClick={double} >{text}</button>
        <button onMouseDown={single} onD={double} >{text}</button>
        </div>
        </>
    );
}

export default App;
