import './App.css';
import React, { useState } from "react";



const App = () =>{
    setInterval(()=> {var curTime = new Date().toLocaleTimeString()
    console.log(curTime);
    const [count,setCount] = useState(curTime);
    const updatTime = () => setCount(curTime);
   },1000)
    return (
        <>
        <div className="count1">
        <h1 className="Heading">{count}</h1>
        <button onClick={updatTime}>click me</button>
        </div>
        </>
    );
}

export default App;
