import './App.css';
import React, { useState } from "react";



const App = () =>{
    let curTime = new Date().toLocaleTimeString();
    console.log(curTime);
    const [time,setTime] = useState(curTime);
    const updatTime = () =>{
        curTime =  new Date().toLocaleTimeString();
        setTime(curTime);
    }
    setInterval(updatTime,1000);

    return (
        <>
        <div className="time1">
        <h1 className="Heading">{time}</h1>

        </div>
        </>
    );
}

export default App;
