import React, { useState } from 'react';
import "./App.css"
const App = () => {
    const [insert,setInsert] = useState([]);
    const [ins,setIns] = useState('');
    const [hide,setHide] = useState(false)
    const delt = () => {
        setHide(false)
    };
    const catchIn = (event) => {
        // console.log(event.target.value);
        setIns(event.target.value);
    };
    const add = () => {
        setHide(true);
        setInsert(()=>{
            return
        });
        setIns('');
    }
    return (
        <>
            <div className="todoList">
                <h1 className="heading">todo list</h1>
                <div className='inputFiled'>
                    <input placeholder='this is input field' value={ins} onChange={catchIn}/><button onClick={add}>+</button>
                </div>
                <ul className='uoList'>
                    <div style={{display:hide&&insert?'block':'none'}} className='btnlist'><button className='btn' onClick={delt}>&times;</button><li className='list'>{insert}</li></div>
                </ul>
            </div>
        </>
    );
}

export default App;
