import React, { useState } from 'react';

import './App.css'

const App = () => {

    const [Incr,setIncr] = useState(0);
    const incr = () => setIncr(Incr+1);
    return (
        <>
            <div className="box">
                <h1 className="heading">Heading {Incr}</h1>
                <div className="btnbox">
                    <button className="incr btn"
onClick={incr()}>increment</button>
                    <button className="decr btn">decrement</button>
                </div>
            </div>
        </>
        );
}
export default App;
