const App = () => {

    const [change,setCh] = useState({
        uname : '',
        pass : '',
        email : ''
    });

    const inputChange = (event) => {
        const {value , name} = event.target;
        setCh((preV)=>{
            if(name === 'Uname'){
                return {[u]:preV}
            }
            else if(name === 'Pass'){
                return {
                    uname : preV.uname,
                    pass : value,
                    email : preV.email,}
            }
            else if(name === 'Email'){
                return {
                    uname :preV.uname,
                    pass : preV.pass,
                    email : value, }
            }
        })
    };

    const submit = (event) => {
        event.preventDefault();
        alert('form submit');
    };

    return (
        <>
        <form onSubmit={submit}>
        <div className='root'>
        <h1 className="Heading">Hello {change.uname} {change.pass}</h1>
        <p>{change.email}</p>
        <input type="text" name = "Uname" onChange = {inputChange} value = {change.uname}/>
        <input type="text" name = "Pass" onChange = {inputChange} value = {change.pass}/>
        <input type="text" name = "Email" onChange = {inputChange} value = {change.email}/>
        <button type='submit'>Submit me</button>
        </div>
        </form>
        </>
    );
}

export default App;
