        setDelt('');
    }
    return (
        <>
            <div className='btnlist'>
                <button className='btn' onClick={cut} onDoubleClick={()=>props.delete(props.id)}>{l}</button><li className='list' style={{textDecoration:line?'line-through':'none',color:line?'gray':'none'}}>{props.value}</li>
            </div>
        </>
    );
}
