import './App.css';
import React, { useState } from "react";



const App = () => {
    const [bgclr,setBg] = useState("#8e44ad");
    const [text,setTxt] = useState("Click Me");
    const single = () => {
        setBg("#34495e");
        // setBg('#8e44ad');
        setTxt("Ouch!! üò±");
    }
    const double = () => {
        setBg('#8e44ad');
        // setBg("#34495e");
        setTxt('Yehh üòè');
    }

    return (
        <>
        <div className='root' style = {{ backgroundColor : bgclr}}>
        {/* <h1 className="Heading">{time}</h1> */}
        <button onClick={single} onDoubleClick={double} >{text}</button>
        <button onMouseDown={single} onMouseUp={double} >{text}</button>
        <button onMouseEnter={single} onMouseOut={double} >{text}</button>
        <button onMouseOver={single} onMouseMove={double} >{text}</button>
        <button onMouseDownCapture={single} onMouseUpCapture={double} >{text}</button>
        <button onMouseLeave={single} onMouseOverCapture={double} >{text}</button>
        <button onMouseLeav={single} onMouseOverCapture={double} >{text}</button>
        </div>
        </>
    );
}

export default App;
