const App = () => {

    const [indc,setInDc] = useState(0);
    const Incr = () => setInDc(indc+1);
    const Decr = () => {
        if(indc!==0)
            setInDc(indc-1)
        else{
            setInDc(indc);
            alert('Sorry for You has reached Zero limit ');
        }
    }
    return (
        <>
            <div className="box">
                <h1 className="heading">{indc}</h1>
                <div className="btnbox">
                
                    <Button className="incr btn" onClick={Incr}><AddIcon/></Button>
                    <Button className="decr btn"  onClick={Decr}><DeleteIcon/></Button>
                </div>
            </div>
        </>
        );
}
export default App;
