import './App.css'
import React, { useState } from 'react';



const App = () => {

    const [indc,setInDc] = useState(0);
    const Incr = () => setInDc(indc+1);
    const Decr = () => {
        if(indc!==0)
            setInDc(indc-1)
        else{
            setInDc(indc);
            alert('Sorry ');
        }
    }
    return (
        <>
            <div className="box">
                <h1 className="heading">{indc}</h1>
                <div className="btnbox">
                    <button className="incr btn" onClick={Incr}>increment</button>
                    <button className="decr btn"  onClick={Decr}>decrement</button>
                </div>
            </div>
        </>
        );
}
export default App;
